import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-category',
  templateUrl: './category.component.html',
  styleUrls: ['./category.component.scss'],
})
export class CategoryComponent {
  public categories: any;
  private originalCategories: any;
  private _filtroLista: string = '';

  public get filtroLista(): string {
    return this._filtroLista;
  }

  public set filtroLista(value: string) {
    this._filtroLista = value;
    this.categories = this.filtroLista
      ? this.filtrarCategorias(this._filtroLista)
      : this.originalCategories;
  }

  filtrarCategorias(filtrarPor: string): any {
    filtrarPor = filtrarPor.toLocaleLowerCase();

    return this.originalCategories.filter(
      (category: { name: string }) =>
        category.name.toLocaleLowerCase().indexOf(filtrarPor) !== -1
    );
  }

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.getCategories();
  }

  public getCategories(): void {
    this.http.get('https://localhost:7054/api/categories').subscribe(
      (response) => {
        this.categories = response;
        this.originalCategories = response;
      },
      (error) => console.log(error)
    );
  }
}
<h2>Categorias</h2>
<hr />

<div class="form-inline">
  <div class="for-group mb-2">
    <input
      type="text"
      class="form-control mr-2"
      placeholder="Buscar"
      [(ngModel)]="filtroLista"
    />
    <button class="btn btn-outline-success">
      <i class="fa-solid fa-magnifying-glass"></i>
    </button>
  </div>
</div>
<h3>Filtro: {{ filtroLista }}</h3>

<table class="table table-striped">
  <thead class="thead-dark">
    <tr>
      <th>#</th>
      <th>Nome</th>
      <th>Opções</th>
    </tr>
  </thead>
  <tbody *ngIf="categories; else nameNotFound">
    <tr *ngFor="let category of categories">
      <td>{{ category.id }}</td>
      <td>{{ category.name }}</td>
      <td>
        <a
          asp-action="Edit"
          class="btn btn-outline-primary"
          asp-route-id="@item.Id"
          ><i class="fa fa-edit"></i
        ></a>
        |
        <a
          asp-action="Excluir"
          class="btn btn-outline-danger"
          asp-route-id="@item.Id"
          ><i class="fa fa-trash"></i
        ></a>
      </td>
    </tr>
  </tbody>
  <ng-template #nameNotFound>
    <tfoot>
      <tr>
        <td colspan="8" class="text-center">
          <h4>Nem uma categoria encontrada!</h4>
        </td>
      </tr>
    </tfoot>
  </ng-template>
</table>
